package training.PFT.sandbox;public class MyFirstProgram55 {  public static void main(String[] args) {    hello("world");    hello("Sveta");    hello("Ula");    hello("Slava");    Square s = new Square(5);        System.out.println("Площадь квадрата со стороной " + s.l + " равна " + s.area() );    Rectangle r = new Rectangle (7,9);        System.out.println("Площадь прямоугольника со сторонами " + r.a + " и " + r.b + " равна " + r.area() );  }  public static void hello(String somebody) {        System.out.println("Hello, " + somebody + "!");  }}